import React, { useState } from 'react';
import { createUser } from '../api/user';

function UserForm() {
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    user_email: '',
    org_id: '',
    user_password: '',
    user_role: 'user'
  });
  const [message, setMessage] = useState('');

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setMessage('');
    try {
      await createUser(form);
      setMessage('User created!');
    } catch (err) {
      setMessage('Error creating user');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Create User</h3>
      <input name="first_name" placeholder="First Name" value={form.first_name} onChange={handleChange} required />
      <input name="last_name" placeholder="Last Name" value={form.last_name} onChange={handleChange} required />
      <input name="user_email" placeholder="Email" value={form.user_email} onChange={handleChange} required />
      <input name="org_id" placeholder="Org ID" value={form.org_id} onChange={handleChange} required />
      <input name="user_password" type="password" placeholder="Password" value={form.user_password} onChange={handleChange} required />
      <select name="user_role" value={form.user_role} onChange={handleChange}>
        <option value="user">User</option>
        <option value="admin">Admin</option>
      </select>
      <button type="submit">Create</button>
      {message && <div>{message}</div>}
    </form>
  );
}

export default UserForm;
